    protected function render() {

		$settings = $this->get_settings_for_display();

		$this->add_render_attribute(
            'socialicons',
            [
                'class' => [
					'mae-social-icons',
					'mae-social-icons-' . $settings['style'] . '-style',
					'mae-icon-' . $settings['shape'],
					'mae-social-icons-inline',
					'mae-social-icons-inline-' . $settings['position'],
					$settings['full_width'],
					'mae-social-icons-' . $settings['color_scheme']
                ]
            ]
        );

		?>

		<div <?php echo $this->get_render_attribute_string('socialicons'); ?>>

			<?php foreach($settings['icons'] as $icon) { ?>

			<?php
				$target = $icon['link']['is_external'] ? ' target="_blank"' : '';
				$nofollow = $icon['link']['nofollow'] ? ' rel="nofollow"' : '';
			?>

			<a href="<?php echo $icon['link']['url']; ?>"<?php echo $target.$nofollow; ?> class="mae-icon mae-icon-<?php echo $icon['social']; ?>">
				<div class="mae-icon-left">
					<?php echo $this->render_icon($icon['social']); ?>
				</div>
			</a>

			<?php } ?>

		</div>

		<?php
        
	}
	
	protected function _content_template() {

		?>

		<#
            view.addRenderAttribute('socialicons', {
                'class': [
					'mae-social-icons',
					'mae-social-icons-' + settings.style + '-style',
					'mae-icon-' + settings.shape,
					'mae-social-icons-inline',
					'mae-social-icons-inline-' + settings.position,
					settings.full_width,
					'mae-social-icons-' + settings.color_scheme
                ],
			});
			
			var icons = {
				<?php foreach($this->render_icon() as $key => $value) { ?>
				'<?php echo $key; ?>': '<?php echo $value; ?>',
				<?php } ?>
			};

		#>
		
		<div {{{ view.getRenderAttributeString('socialicons') }}}>
		<# _.each(settings.icons, function(icon) { #>

		<#
			var target = icon.link.is_external ? ' target="_blank"' : '';
			var nofollow = icon.link.nofollow ? ' rel="nofollow"' : '';
		#>

		<a href="{{ icon.link.url }}"{{ target }}{{ nofollow }} class="mae-icon mae-icon-{{ icon.social }}">
			<div class="mae-icon-left">{{{ icons[icon.social] }}}</div>
		</a>

		<# }); #>
		</div>

		<?php

	}

